import React, {Component} from "react";
import Head from './component/Head'
import List from './component/List'
import Footer from './component/Footer'

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            todos:[
                {id:1, title:'看书一小时', finished:false},
                {id:2, title:'跑步40分钟', finished:false},
                {id:3, title:'完成React学习', finished:false},
                {id:4, title:'给兰花浇水', finished:false}
            ],
            finishContent:[]
        }
    }
    render() {
        const {todos} = this.state;
        return (
            <div class="todo-container">
                <div class="todo-wrap">
                    {/*头部*/}
                    <Head/>
                    {/*列表*/}
                    <List
                        todos={todos}
                        removeTodo={this._removeTodoWithId}
                    />
                    {/*尾部*/}
                    <Footer/>
                </div>
            </div>
        );
    }

    _removeTodoWithId = (todoId)=>{
        const tempTodos = this.state.todos;
        let checkedCount = 0;
        tempTodos.forEach((todo,index)=>{
            if (todo.id === todoId){
                tempTodos.splice(index,1);
            }
        });
        // 处理选中
        tempTodos.forEach((todo,index)=>{
            if(todo.finished){
                checkedCount+=1;
            }
        });
        this.setState({
            todos:tempTodos,
            finishContent:checkedCount
        })
    };

    _changeTodoChecked=(todoId,checked)=>{
        const tempTodos = this.state.todos;
        let checkedCount = 0;
        tempTodos.forEach((todo,index)=>{
            if (todo.id === todoId){
                todo.finished = checked;
            }
            if(todo.finished){
                checkedCount+=1;
            }
        });
    }
}

export default App;
